mapper.select:
select

  distinct


from common_user_info




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

mapper.update:
update common_user_info
set
  last_Modify_Time = now(),
  coni_count = coni_count + #{coin}
where sys_uni_id = #{userUinId}

update common_user_info
set
  last_Modify_Time = now(),
  coni_count = coni_count - #{coin}
where sys_uni_id = #{userUinId} and coni_count >= coni_count - #{coin}
